// Helper function to execute queries using @vercel/postgres
// @vercel/postgres uses template literals natively, but we support both template literals and parameterized queries
// For parameterized queries ($1, $2, etc), we use sql.query if available
async function executeQuery(query, params = []) {
  // If no params, use template literal directly
  if (!params || params.length === 0) {
    // Convert regular string query to template literal
    return await sql.query(query);
  }
  
  // For parameterized queries, check if sql.query exists
  // Some versions of @vercel/postgres support sql.query() with parameters
  if (typeof sql.query === 'function') {
    try {
      return await sql.query(query, params);
    } catch (err) {
      // If sql.query fails, it might not support parameters
      // Fall through to manual conversion
      console.warn('sql.query() with parameters failed, attempting manual conversion');
    }
  }
  
  // Manual conversion: replace $1, $2, etc with actual values
  // This is a basic implementation - for production, consider using a proper query builder
  let convertedQuery = query;
  params.forEach((param, index) => {
    const placeholder = `$${index + 1}`;
    const value = typeof param === 'string' ? `'${param.replace(/'/g, "''")}'` : param;
    convertedQuery = convertedQuery.replace(new RegExp(`\\${placeholder}(?!\\d)`, 'g'), value);
  });
  
  return await sql.query(convertedQuery);
}

