// Refresh missions for dashboard (assigns missions to all users until each has 3)
app.post('/api/missions/refresh', async (req, res) => {
  try {
    console.log('Refreshing missions for all users...');
    
    // First, repopulate the database by resetting ALL missions to available status
    await executeQuery(`
      UPDATE missions 
      SET completed = false, assigned_now = false, assigned_agent = null, 
          past_assigned_agents = array[]::integer[], mission_expires = null
    `);
    
    // Reset object_missions to available status
    await executeQuery(`
      UPDATE object_missions 
      SET completed = false, assigned_now = false, assigned_agent = null, 
          past_assigned_agents = array[]::integer[]
    `);
    
    // Reset passphrase_missions to available status
    await executeQuery(`
      UPDATE passphrase_missions 
      SET completed = false, assigned_receiver = null, assigned_sender_1 = null, 
          assigned_sender_2 = null, previous_receivers = array[]::integer[], 
          previous_senders = array[]::integer[]
    `);
    
    // Reset book_missions to available status
    await executeQuery(`
      UPDATE book_missions 
      SET red_completed = false, blue_completed = false, assigned_red = null, 
          assigned_blue = null, previous_reds = array[]::integer[], 
          previous_blues = array[]::integer[]
    `);
    
    console.log('Database repopulated - all missions reset to available');
    
    // Get all users who are present
    const usersResult = await executeQuery('SELECT id, team FROM users WHERE ishere = true ORDER BY id');
    const users = (usersResult.rows || usersResult);
    
    if (users.length === 0) {
      return res.status(400).json({ error: 'No users available' });
    }
    
    // Track mission counts and types per user
    const missionCounts = new Map();
    const missionTypesByUser = new Map(); // { userId: ['book', 'passphrase', ...] }
    users.forEach(user => {
      missionCounts.set(user.id, 0);
      missionTypesByUser.set(user.id, []);
    });
    
    const assignedMissionsByUser = new Map();
    users.forEach(user => assignedMissionsByUser.set(user.id, []));
    
    // Helper function to pick random element
    const pick = (arr) => arr[Math.floor(Math.random() * arr.length)];
    
    // Helper function to get available users for assignment
    const getAvailableUsers = (excludeIds = []) => {
      return users.filter(u => 
        !excludeIds.includes(u.id) && 
        (missionCounts.get(u.id) || 0) < 3
      );
    };
    
    // Helper function to refresh available missions from database
    const refreshAvailableMissions = async () => {
      const bookResult = await executeQuery('SELECT * FROM book_missions WHERE assigned_red IS NULL OR assigned_blue IS NULL ORDER BY RANDOM()');
      const passphraseResult = await executeQuery('SELECT * FROM passphrase_missions WHERE assigned_receiver IS NULL AND assigned_sender_1 IS NULL AND assigned_sender_2 IS NULL ORDER BY RANDOM()');
      return {
        book: (bookResult.rows || bookResult),
        passphrase: (passphraseResult.rows || passphraseResult)
      };
    };
    
    // Helper function to determine which mission type user needs
    const getNeededMissionType = (userId) => {
      const userMissions = missionTypesByUser.get(userId) || [];
      const bookCount = userMissions.filter(t => t === 'book').length;
      const passphraseCount = userMissions.filter(t => t === 'passphrase').length;
      
      // If user has 0 missions, prefer giving them a book mission first (2-person)
      // If user has 1 mission and it's book, give them passphrase
      // If user has 1 mission and it's passphrase, give them book
      // If user has 2 missions, give them the type they have fewer of
      
      if (userMissions.length === 0) {
        return 'book'; // Start with book missions
      } else if (userMissions.length === 1) {
        return userMissions[0] === 'book' ? 'passphrase' : 'book';
      } else if (userMissions.length === 2) {
        return bookCount < passphraseCount ? 'book' : 'passphrase';
      }
      return null; // User has 3 missions already
    };
    
    let availableMissions = await refreshAvailableMissions();
    
    // Main assignment loop - iterate until all users have 3 missions
    let iterations = 0;
    const maxIterations = users.length * 10; // Safety limit
    
    while (iterations < maxIterations) {
      iterations++;
      
      // Find users who still need missions
      const usersNeedingMissions = users.filter(u => (missionCounts.get(u.id) || 0) < 3);
      
      if (usersNeedingMissions.length === 0) {
        break; // All users have 3 missions
      }
      
      // Refresh available missions periodically
      if (iterations % 5 === 0) {
        availableMissions = await refreshAvailableMissions();
      }
      
      // Try to assign a mission to a random user who needs one
      const user = pick(usersNeedingMissions);
      const neededType = getNeededMissionType(user.id);
      
      let assigned = false;
      
      // Randomly choose between book and passphrase if both are available and user could use either
      // This ensures a mix of both types throughout the assignment process
      const canUseBook = neededType === 'book' || (neededType !== 'passphrase' && availableMissions.book.length > 0);
      const canUsePassphrase = neededType === 'passphrase' || (neededType !== 'book' && availableMissions.passphrase.length > 0);
      
      let missionTypeToTry = neededType;
      
      // If user could use either type, randomly choose to ensure mix
      if (canUseBook && canUsePassphrase && availableMissions.book.length > 0 && availableMissions.passphrase.length > 0) {
        missionTypeToTry = Math.random() < 0.5 ? 'book' : 'passphrase';
      } else if (canUsePassphrase && availableMissions.passphrase.length > 0) {
        missionTypeToTry = 'passphrase';
      } else if (canUseBook && availableMissions.book.length > 0) {
        missionTypeToTry = 'book';
      }
      
      // Try to assign based on chosen type
      if (missionTypeToTry === 'book' && availableMissions.book.length > 0) {
        // Find a book mission for this user's team
        let bookMission = availableMissions.book.find(m => {
          if (user.team === 'red' && m.assigned_red === null) return true;
          if (user.team === 'blue' && m.assigned_blue === null) return true;
          return false;
        });
        
        // If no team-specific match, find any book mission needing assignment
        if (!bookMission) {
          bookMission = availableMissions.book.find(m => 
            (m.assigned_red === null && user.team === 'red') || 
            (m.assigned_blue === null && user.team === 'blue')
          );
        }
        
        // If still no match, find any book mission that needs both assignments
        if (!bookMission) {
          bookMission = availableMissions.book.find(m => 
            m.assigned_red === null || m.assigned_blue === null
          );
        }
        
        if (bookMission) {
          // Find a partner for the opposite team
          const oppositeTeam = user.team === 'red' ? 'blue' : 'red';
          const availableOppositeTeam = getAvailableUsers([user.id]).filter(u => u.team === oppositeTeam);
          
          if (availableOppositeTeam.length > 0) {
            const partner = pick(availableOppositeTeam);
            
            // Assign both users
            if (user.team === 'red') {
              await executeQuery(`
                UPDATE book_missions 
                SET assigned_red = $1, previous_reds = array_append(COALESCE(previous_reds, ARRAY[]::integer[]), $1),
                    assigned_blue = $2, previous_blues = array_append(COALESCE(previous_blues, ARRAY[]::integer[]), $2)
                WHERE id = $3
              `, [user.id, partner.id, bookMission.id]);
            } else {
              await executeQuery(`
                UPDATE book_missions 
                SET assigned_blue = $1, previous_blues = array_append(COALESCE(previous_blues, ARRAY[]::integer[]), $1),
                    assigned_red = $2, previous_reds = array_append(COALESCE(previous_reds, ARRAY[]::integer[]), $2)
                WHERE id = $3
              `, [user.id, partner.id, bookMission.id]);
            }
            
            missionCounts.set(user.id, (missionCounts.get(user.id) || 0) + 1);
            missionCounts.set(partner.id, (missionCounts.get(partner.id) || 0) + 1);
            missionTypesByUser.get(user.id).push('book');
            missionTypesByUser.get(partner.id).push('book');
            
            assignedMissionsByUser.get(user.id).push({ ...bookMission, mission_type: 'book' });
            assignedMissionsByUser.get(partner.id).push({ ...bookMission, mission_type: 'book' });
            
            // Remove from available
            availableMissions.book = availableMissions.book.filter(m => m.id !== bookMission.id);
            assigned = true;
          }
        }
      }
      
      // Try to assign passphrase mission if that's what was chosen or as fallback
      if ((missionTypeToTry === 'passphrase' || !assigned) && availableMissions.passphrase.length > 0) {
        const passphraseMission = availableMissions.passphrase[0];
        const availableForSenders = getAvailableUsers([user.id]);
        
        if (availableForSenders.length >= 2) {
          const sender1 = pick(availableForSenders);
          const remainingAfterSender1 = availableForSenders.filter(u => u.id !== sender1.id);
          const sender2 = pick(remainingAfterSender1);
          
          await executeQuery(`
            UPDATE passphrase_missions 
            SET assigned_receiver = $1, 
                assigned_sender_1 = $2,
                assigned_sender_2 = $3,
                previous_receivers = array_append(COALESCE(previous_receivers, ARRAY[]::integer[]), $1),
                previous_senders = array_append(array_append(COALESCE(previous_senders, ARRAY[]::integer[]), $2), $3)
            WHERE id = $4
          `, [user.id, sender1.id, sender2.id, passphraseMission.id]);
          
          missionCounts.set(user.id, (missionCounts.get(user.id) || 0) + 1);
          missionCounts.set(sender1.id, (missionCounts.get(sender1.id) || 0) + 1);
          missionCounts.set(sender2.id, (missionCounts.get(sender2.id) || 0) + 1);
          
          missionTypesByUser.get(user.id).push('passphrase');
          missionTypesByUser.get(sender1.id).push('passphrase');
          missionTypesByUser.get(sender2.id).push('passphrase');
          
          assignedMissionsByUser.get(user.id).push({ ...passphraseMission, mission_type: 'passphrase' });
          assignedMissionsByUser.get(sender1.id).push({ ...passphraseMission, mission_type: 'passphrase' });
          assignedMissionsByUser.get(sender2.id).push({ ...passphraseMission, mission_type: 'passphrase' });
          
          availableMissions.passphrase = availableMissions.passphrase.filter(m => m.id !== passphraseMission.id);
          assigned = true;
        }
      }
      
      // If no assignment was made this iteration, break to avoid infinite loop
      if (!assigned && (availableMissions.book.length === 0 && availableMissions.passphrase.length === 0)) {
        console.log('No more missions available and no assignments made');
        break;
      }
    }
    
    // Convert to response format
    const missionAssignments = {};
    assignedMissionsByUser.forEach((missions, userId) => {
      missionAssignments[userId] = missions;
    });
    
    console.log(`Successfully assigned missions to ${users.length} users`);
    res.json(missionAssignments);
  } catch (err) {
    console.error('Error refreshing missions:', err);
    res.status(500).json({ error: 'Failed to refresh missions' });
  }
});

// Assign passphrase missions - using EXACT same pattern as book missions
async function assignPassphraseMissions() {
  // Get available agents - same as book missions
  const usersResult = await executeQuery('SELECT id FROM users WHERE ishere = true', []);
  const users = (usersResult.rows || usersResult).map(u => u.id);

  if (users.length < 3) {
    return { assigned: 0, reason: 'Need at least 3 available agents' };
  }

  // Fetch all passphrase missions - same pattern as book missions
  const missionsResult = await executeQuery('SELECT id, previous_receivers, previous_senders FROM passphrase_missions ORDER BY id');
  const missions = missionsResult.rows || missionsResult;

  // Helper to pick a random element - same as book missions
  const pick = (arr) => arr[Math.floor(Math.random() * arr.length)];

  // Current assignment counts per user - same pattern as book missions
  const receiverCountsRes = await executeQuery(
    `SELECT assigned_receiver AS user_id, COUNT(*)::int AS cnt
     FROM passphrase_missions WHERE assigned_receiver IS NOT NULL GROUP BY assigned_receiver`
  );
  const senderCountsRes = await executeQuery(
    `SELECT assigned_sender_1 AS user_id, COUNT(*)::int AS cnt
     FROM passphrase_missions WHERE assigned_sender_1 IS NOT NULL GROUP BY assigned_sender_1
     UNION ALL
     SELECT assigned_sender_2 AS user_id, COUNT(*)::int AS cnt
     FROM passphrase_missions WHERE assigned_sender_2 IS NOT NULL GROUP BY assigned_sender_2`
  );
  
  const receiverCounts = receiverCountsRes.rows || receiverCountsRes;
  const senderCounts = senderCountsRes.rows || senderCountsRes;
  const receiverCountMap = new Map(receiverCounts.map(r => [r.user_id, Number(r.cnt)]));
  const senderCountMap = new Map();
  senderCounts.forEach(s => {
    senderCountMap.set(s.user_id, (senderCountMap.get(s.user_id) || 0) + Number(s.cnt));
  });

  let updates = 0;
  // Assign each mission to one receiver and two senders - EXACT same pattern as book missions
  for (const m of missions) {
    const prevReceivers = Array.isArray(m.previous_receivers) ? m.previous_receivers : [];
    const prevSenders = Array.isArray(m.previous_senders) ? m.previous_senders : [];

    // Get eligible users - same logic as book missions but for 3 people
    const eligibleReceivers = users.filter(id => 
      !prevReceivers.includes(id) && 
      (receiverCountMap.get(id) || 0) < 3
    );
    
    const eligibleSenders = users.filter(id => 
      !prevSenders.includes(id) && 
      (senderCountMap.get(id) || 0) < 3
    );

    const receiverId = eligibleReceivers.length ? pick(eligibleReceivers) : null;
    const availableSenders = eligibleSenders.filter(id => id !== receiverId);
    
    if (receiverId !== null && availableSenders.length >= 2) {
      const sender1 = pick(availableSenders);
      const sender2 = pick(availableSenders.filter(id => id !== sender1));
      
      // Assign receiver and two senders - EXACT same UPDATE pattern as book missions
      await executeQuery(
        `UPDATE passphrase_missions
         SET assigned_receiver = $1,
             assigned_sender_1 = $2,
             assigned_sender_2 = $3,
             previous_receivers = array_append(COALESCE(previous_receivers, ARRAY[]::integer[]), $1),
             previous_senders = array_append(array_append(COALESCE(previous_senders, ARRAY[]::integer[]), $2), $3)
         WHERE id = $4`,
        [receiverId, sender1, sender2, m.id]
      );
      updates++;
      receiverCountMap.set(receiverId, (receiverCountMap.get(receiverId) || 0) + 1);
      senderCountMap.set(sender1, (senderCountMap.get(sender1) || 0) + 1);
      senderCountMap.set(sender2, (senderCountMap.get(sender2) || 0) + 1);
    }
  }

  return { assigned: updates };
}

