// Test endpoint for passphrase mission assignments only
app.post('/api/missions/test-passphrase', async (req, res) => {
  try {
    console.log('Testing passphrase mission assignments...');
    console.log('Using Vercel Postgres database');
    console.log('POSTGRES_URL set:', !!process.env.POSTGRES_URL);
    console.log('DATABASE_URL set:', !!process.env.DATABASE_URL);
    
    // Reset passphrase_missions to available status
    await executeQuery(`
      UPDATE passphrase_missions 
      SET completed = false, assigned_receiver = null, assigned_sender_1 = null, 
          assigned_sender_2 = null, previous_receivers = array[]::integer[], 
          previous_senders = array[]::integer[]
    `);
    
    // Get all users who are present
    const usersResult = await executeQuery('SELECT id, team, firstname, lastname FROM users WHERE ishere = true ORDER BY id');
    const users = (usersResult.rows || usersResult);
    
    if (users.length === 0) {
      return res.status(400).json({ error: 'No users available' });
    }
    
    // Get available passphrase missions
    const passphraseMissionsResult = await executeQuery('SELECT * FROM passphrase_missions WHERE assigned_receiver IS NULL AND assigned_sender_1 IS NULL AND assigned_sender_2 IS NULL ORDER BY RANDOM()');
    const availablePassphraseMissions = (passphraseMissionsResult.rows || passphraseMissionsResult);
    
    console.log(`Found ${users.length} users and ${availablePassphraseMissions.length} available passphrase missions`);
    
    const assignments = [];
    const pick = (arr) => arr[Math.floor(Math.random() * arr.length)];
    
    // Assign passphrase missions one by one
    for (const mission of availablePassphraseMissions) {
      if (users.length < 3) {
        console.log(`Not enough users (${users.length}) to assign passphrase mission ${mission.id}`);
        break;
      }
      
      // Pick a random user as receiver
      const receiver = pick(users);
      const remainingUsers = users.filter(u => u.id !== receiver.id);
      
      if (remainingUsers.length < 2) {
        console.log(`Not enough remaining users to assign senders for mission ${mission.id}`);
        break;
      }
      
      // Pick 2 random senders
      const sender1 = pick(remainingUsers);
      const remainingAfterSender1 = remainingUsers.filter(u => u.id !== sender1.id);
      const sender2 = pick(remainingAfterSender1);
      
      console.log(`Assigning mission ${mission.id}:`);
      console.log(`  Receiver: ${receiver.id} (${receiver.firstname} ${receiver.lastname})`);
      console.log(`  Sender 1: ${sender1.id} (${sender1.firstname} ${sender1.lastname})`);
      console.log(`  Sender 2: ${sender2.id} (${sender2.firstname} ${sender2.lastname})`);
      
      // Assign receiver and senders
      const result = await executeQuery(`
        UPDATE passphrase_missions 
        SET assigned_receiver = $1, 
            assigned_sender_1 = $2,
            assigned_sender_2 = $3,
            previous_receivers = array_append(COALESCE(previous_receivers, ARRAY[]::integer[]), $1),
            previous_senders = array_append(array_append(COALESCE(previous_senders, ARRAY[]::integer[]), $2), $3)
        WHERE id = $4 RETURNING *
      `, [receiver.id, sender1.id, sender2.id, mission.id]);
      
      const updated = result.rows || result;
      const assignment = {
        mission_id: mission.id,
        mission_template: mission.passphrase_template,
        receiver: {
          id: receiver.id,
          name: `${receiver.firstname} ${receiver.lastname}`
        },
        sender_1: {
          id: sender1.id,
          name: `${sender1.firstname} ${sender1.lastname}`
        },
        sender_2: {
          id: sender2.id,
          name: `${sender2.firstname} ${sender2.lastname}`
        },
        updated_mission: updated[0]
      };
      
      assignments.push(assignment);
    }
    
    // Verify assignments by querying the database
    const verificationResult = await executeQuery('SELECT id, passphrase_template, assigned_receiver, assigned_sender_1, assigned_sender_2 FROM passphrase_missions ORDER BY id');
    const allMissions = (verificationResult.rows || verificationResult);
    
    res.json({
      message: `Assigned ${assignments.length} passphrase missions`,
      assignments: assignments,
      verification: allMissions,
      summary: {
        total_missions: allMissions.length,
        assigned_missions: allMissions.filter(m => m.assigned_receiver !== null).length,
        unassigned_missions: allMissions.filter(m => m.assigned_receiver === null).length
      }
    });
  } catch (err) {
    console.error('Error testing passphrase mission assignments:', err);
    res.status(500).json({ error: 'Failed to test passphrase mission assignments', details: err.message });
  }
});

// Assign mission to agent
app.put('/api/missions/:id/assign', async (req, res) => {
  try {
    const { id } = req.params;
    const { agentId } = req.body;
    
    // Calculate expiration time (15 minutes from now)
    const expirationTime = new Date(Date.now() + 15 * 60 * 1000);
    
    // Update mission assignment
    const result = await executeQuery(`
      UPDATE missions 
      SET assigned_agent = $1, assigned_now = true, past_assigned_agents = array_append(past_assigned_agents, $1), mission_expires = $2
      WHERE id = $3 RETURNING *
    `, [agentId, expirationTime, id]);
    
    const missions = result.rows || result;
    res.json(missions[0]);
  } catch (err) {
    console.error('Error assigning mission:', err);
    res.status(500).json({ error: 'Failed to assign mission' });
  }
});

// Complete mission
app.put('/api/missions/:id/complete', async (req, res) => {
  try {
    const { id } = req.params;
    const { successKey, teamPoints } = req.body;
    
    // Get the mission to check the success key
    const missionResult = await executeQuery('SELECT * FROM missions WHERE id = $1', [id]);
    const missions = missionResult.rows || missionResult;
    
    if (missions.length === 0) {
      return res.status(404).json({ error: 'Mission not found' });
    }
    
    const mission = missions[0];
    
    // Check if mission is already completed
    if (mission.completed) {
      return res.status(400).json({ error: 'Mission already completed' });
    }
    
    // Validate success key (case-insensitive comparison)
    const expectedKey = mission.success_key?.toLowerCase().trim();
    const providedKey = successKey?.toLowerCase().trim();
    
    if (!expectedKey || !providedKey) {
      return res.status(400).json({ error: 'Success key required' });
    }
    
    // Check if success key matches (exact match or contains expected key for flexible answers)
    const isCorrect = expectedKey === providedKey || 
                     expectedKey.includes(providedKey) || 
                     providedKey.includes(expectedKey) ||
                     (expectedKey.startsWith('[') && expectedKey.endsWith(']')); // For placeholder answers like [any codename]
    
    if (!isCorrect) {
      return res.status(400).json({ error: 'Incorrect success key' });
    }
    
    // Mark mission as completed
    await executeQuery('UPDATE missions SET completed = true, assigned_now = false WHERE id = $1', [id]);
    
    // Update team points if provided
    if (teamPoints && teamPoints.team) {
      await executeQuery('UPDATE teams SET points = points + $1 WHERE name = $2', [teamPoints.points, teamPoints.team]);
    }
    
    res.json({ message: 'Mission completed successfully' });
  } catch (err) {
    console.error('Error completing mission:', err);
    res.status(500).json({ error: 'Failed to complete mission' });
  }
});

